func TestGraphDefinition(t *testing.T) {
	var mongodb MongoDBPlugin

	graphdef := mongodb.GraphDefinition()
	if len(graphdef) != 2 {
		t.Errorf("GetTempfilename: %d should be 4", len(graphdef))
	}
}

